import { BrowserRouter, NavLink, Routes, Route } from "react-router-dom";
import Home from "./Home";
import Register from "./Register";
import Login from "./Login";
import PremiumContent from "./PremiumContent";
import PrivateRoute from "./routes/PrivateRoute";
import PublicRoute from "./routes/PublicRoute";
import React, { useState,useEffect } from "react";
import { getUser, getToken, setUserSession, resetUserSession } from "./service/AuthService";
import axios from "axios";

const verifyTokenAPIURL = 'https://2noo61wwr8.execute-api.us-east-2.amazonaws.com/staging/verify';

function App() {

  const [isAuthenticating,setAuthenticating]=useState

  useEffect(() => {
    const token = getToken();
    if (token === 'undefined' || token === undefined || token === null || !token) {
      return;
    }

    // HTTP request configuration, including API key header
    // Note: It's better to store API keys in environment variables instead of in code
    const requestConfig = {
      headers: {
        'x-api-key': 'v9aez83lFu3PZMQrCQRqOaKGhCkxDwOq5pYaLuJy'
      }
    };
    const requestBody = {
      user: getUser(),
      token: token

    }
    axios.post(verifyTokenAPIURL, requestBody, requestConfig).then(response => {
      setUserSession(response.data.user, response.data.token);
    }).catch(() => {
      resetUserSession();
    })
  },[]);
  return (
    <div className="App">
      <BrowserRouter>
        <div className="header">
          <NavLink className={({ isActive }) => isActive ? "active" : ""} to="/">Home</NavLink>
          <NavLink className={({ isActive }) => isActive ? "active" : ""} to="/register">Register</NavLink>
          <NavLink className={({ isActive }) => isActive ? "active" : ""} to="/login">Login</NavLink>
          <NavLink className={({ isActive }) => isActive ? "active" : ""} to="/premium-content">Premium Content</NavLink>
        </div>
        <div className="content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/register" element={<PublicRoute><Register /></PublicRoute>} />
            <Route path="/login" element={<PublicRoute><Login /></PublicRoute>} />
            <Route path="/premium-content" element={<PrivateRoute><PremiumContent /></PrivateRoute>} />
          </Routes>
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
